    tileSize = 16
    position = 0
    
    screenWidth = 480
    screenHeight = 320

    surfacesPassed = 0
    lastSurfaceRedraw = 0

    def __init__(self):
        
        self.spritesheet = Spritesheet.Spritesheet("assets/spritesheet.png", pygame.Rect(0,0,16,16))
        self.surface1 = pygame.Surface([self.screenWidth + (self.tileSize * 10), self.screenHeight])
        self.surface2 = pygame.Surface([self.screenWidth + (self.tileSize * 10), self.screenHeight])

        # try to get rid of this...
        self.buildSurfaces(1)
        self.buildSurfaces(2)
    
    def buildSurfaces(self, surfaceToRebuild):

        if(surfaceToRebuild == 1):
            startColumn = int(self.position / self.tileSize) + self.surfacesPassed * 40
            endColumn = int(startColumn + (self.screenWidth / self.tileSize) + 10)
            
            #print(str(startColumn) + ' - 1')
            #print(self.tiles[0:self.screenHeight/self.tileSize, startColumn:endColumn])

            #self.surface1.fill([0,0,200])
            #del self.surface1
            self.surface1 = pygame.Surface([self.screenWidth + (self.tileSize * 10), self.screenHeight])

            for (row, rowTiles) in enumerate(self.tiles[0:self.screenHeight/self.tileSize, startColumn:endColumn]):
                for (column, tile) in enumerate(rowTiles):
                    self.surface1.blit(self.spritesheet.getTile(tile), (column * self.tileSize - self.position, row * self.tileSize))
                    
            font = pygame.font.SysFont("comicsansms", 72)
            text = font.render("Surface 1", True, (0, 128, 0))
            self.surface1.blit(text,(100, 100))

        else:
            startColumn = int((self.position / self.tileSize) + (self.screenWidth / self.tileSize) + 10) + self.surfacesPassed * 40
            endColumn = int(startColumn + (self.screenWidth / self.tileSize) + 10)

            #print(str(startColumn) + ' - 2')
            print(self.tiles[0:self.screenHeight/self.tileSize, startColumn:endColumn])

            #self.surface1.fill([200,0,0])
            
            for (row, rowTiles) in enumerate(self.tiles[0:self.screenHeight/self.tileSize, startColumn:endColumn]):
                for (column, tile) in enumerate(rowTiles):
                    self.surface2.blit(self.spritesheet.getTile(tile), (column * self.tileSize - self.position, row * self.tileSize))

            font = pygame.font.SysFont("comicsansms", 72)
            text = font.render("Surface 2", True, (0, 128, 0))
            self.surface2.blit(text,(100, 100))


    def redrawSurfaces(self):
        
        if(self.surfacesPassed != self.lastSurfaceRedraw):

            if(self.getCurrentSurface() == 1):
                print('redraw 2')
                self.buildSurfaces(2)
            else:
                print('redraw 1')
                self.buildSurfaces(1)

            self.lastSurfaceRedraw = self.surfacesPassed

    def getCurrentSurface(self):

        if(self.surfacesPassed % 2 == 0):
            return 1
        return 2

    def pushLevel(self, hero):

        if(hero.position[0] >= self.screenWidth * 0.65 and hero.speed[0] > 0):
            self.position += hero.speed[0]

    def update(self, hero):
        
        self.surfacesPassed = int(self.position / (self.screenWidth + (self.tileSize * 10)))
        self.pushLevel(hero)

        if(self.lastSurfaceRedraw < self.surfacesPassed and self.position > self.screenWidth + 160):
            self.redrawSurfaces()