    tileSize = 16
    tileBuffer = 5
    position = 0
    
    screenWidth = 480
    screenHeight = 320

    def __init__(self):

        self.spritesheet = Spritesheet.Spritesheet("assets/spritesheet.png", pygame.Rect(0,0,16,16))
        self.surface = pygame.Surface([self.screenWidth, self.screenHeight]) 

        self.rects = []

        for (row, rowTiles) in enumerate(self.tiles):
            for (column, tile) in enumerate(rowTiles):
                if(tile > 0):
                    self.rects.append(pygame.Rect(column * self.tileSize, row * self.tileSize, self.tileSize, self.tileSize))

    def update(self, hero):
        return False

    def collisionCheck(self, objectTargetPosition):
        collideIndex = objectTargetPosition.collidelist(self.rects)
        print(collideIndex)
        return collideIndex > -1

        
    def render(self):

        startColumn = int(self.position / self.tileSize)
        endColumn = int(startColumn + (self.screenWidth / self.tileSize) + self.tileBuffer)
        
        for (row, rowTiles) in enumerate(self.tiles[0:self.screenHeight/self.tileSize, startColumn:endColumn]):
            for (column, tile) in enumerate(rowTiles):
                self.surface.blit(self.spritesheet.getTile(tile), (column * self.tileSize, row * self.tileSize))

        return self.surface