    tileSize = 16
    tileBuffer = 10
    position = 0
    
    screenWidth = 480
    screenHeight = 320

    def __init__(self):

        self.spritesheet = Spritesheet.Spritesheet("assets/spritesheet.png", pygame.Rect(0,0,16,16))
        self.surface = pygame.Surface([self.screenWidth + (self.tileSize * self.tileBuffer), self.screenHeight]) 
    
    def update(self, hero):
                
        if(hero.position[0] >= self.screenWidth * 0.65 and hero.speed[0] > 0):
            self.position += hero.speed[0]
        elif(hero.position[0] <= 0 and hero.speed[0] < 0):
            self.position += hero.speed[0]
        
    def render(self):

        startColumn = int(self.position / self.tileSize)
        endColumn = int(startColumn + (self.screenWidth / self.tileSize) + self.tileBuffer)
        
        for (row, rowTiles) in enumerate(self.tiles[0:self.screenHeight/self.tileSize, startColumn:endColumn]):
            for (column, tile) in enumerate(rowTiles):
                self.surface.blit(self.spritesheet.getTile(tile), (column * self.tileSize, row * self.tileSize))

        return self.surface